import { useState, useEffect } from "react";

const moods = [
  { emoji: "üòä", label: "Happy" },
  { emoji: "üò¢", label: "Sad" },
  { emoji: "üò°", label: "Angry" },
  { emoji: "üò¥", label: "Tired" },
  { emoji: "üòü", label: "Anxious" }
];

// Motivational quotes based on mood
const moodQuotes = {
  Happy: "Keep smiling! Life is beautiful. üåû",
  Sad: "It's okay to feel sad. Better days are ahead. üåà",
  Angry: "Take a deep breath. Calmness is power. üåø",
  Tired: "Rest is important. Recharge and go again! üí§",
  Anxious: "You are stronger than you think. Take it one step at a time. üíô"
};

export default function MoodTracker() {
  const [selectedMood, setSelectedMood] = useState(null);
  const [message, setMessage] = useState("");
  const [quote, setQuote] = useState("");
  const [journal, setJournal] = useState("");
  const [moodHistory, setMoodHistory] = useState([]);

  useEffect(() => {
    fetchMoodHistory();
  }, []);

  const fetchMoodHistory = async () => {
    try {
      const response = await fetch("http://localhost:3000/moods/history", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setMoodHistory(data);
      }
    } catch (error) {
      console.error("Error fetching mood history:", error);
    }
  };

  const logMood = async (mood) => {
    const date = new Date().toISOString();

    try {
      const response = await fetch("http://localhost:3000/moods/log", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ mood, date, journal })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("Mood logged successfully!");
        setMoodHistory((prev) => [data.mood, ...prev]);
      } else {
        setMessage(data.message || "Failed to log mood.");
      }
    } catch (error) {
      setMessage("Error logging mood.");
    }

    setSelectedMood(mood);
    setQuote(moodQuotes[mood]);
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", fontFamily: "Arial" }}>
      <h3>How do you feel today?</h3>
      <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
        {moods.map((mood, index) => (
          <button
            key={index}
            onClick={() => logMood(mood.label)}
            style={{
              fontSize: "2rem",
              padding: "10px",
              borderRadius: "50%",
              cursor: "pointer",
              background: selectedMood === mood.label ? "#ddd" : "#fff",
              border: "1px solid #ccc"
            }}
          >
            {mood.emoji}
          </button>
        ))}
      </div>

      {selectedMood && (
        <div style={{ marginTop: "20px" }}>
          <p>You selected: <strong>{selectedMood}</strong></p>
          <p style={{ fontStyle: "italic", color: "#555" }}>‚ú® {quote}</p>
          <textarea
            value={journal}
            onChange={(e) => setJournal(e.target.value)}
            placeholder="Write something about your day..."
            style={{
              width: "80%",
              height: "80px",
              marginTop: "10px",
              padding: "5px",
              borderRadius: "5px"
            }}
          />
          <br />
          <button
            onClick={() => logMood(selectedMood)}
            style={{
              marginTop: "10px",
              padding: "8px 16px",
              background: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer"
            }}
          >
            Save Mood & Journal
          </button>
        </div>
      )}

      {message && <p>{message}</p>}

      <h4 style={{ marginTop: "30px" }}>Your Mood History (Last 7 Days)</h4>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {moodHistory.length > 0 ? (
          moodHistory.map((entry, index) => (
            <li key={index} style={{ marginBottom: "5px" }}>
              {entry.date.split("T")[0]} - {entry.mood}
            </li>
          ))
        ) : (
          <p>No mood logs found.</p>
        )}
      </ul>
    </div>
  );
}
