import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "../styles/Reminders.css";

const socket = io("http://localhost:3000"); // ‚úÖ Connect to backend WebSocket

function Reminders() {
  const [reminders, setReminders] = useState([]);
  const [newReminder, setNewReminder] = useState({ date: "", time: "", note: "" });
  const [notifications, setNotifications] = useState([]); // ‚úÖ Store real-time notifications

  useEffect(() => {
    // ‚úÖ Listen for Reminder Notifications
    socket.on("reminderNotification", (data) => {
      setNotifications((prev) => [...prev, data.message]);
    });

    return () => {
      socket.off("reminderNotification");
    };
  }, []);
/*
  // ‚úÖ Fetch Reminders from Backend
  const fetchReminders = async () => {
    try {
      const response = await fetch("http://localhost:3000/api/reminders");
      if (response.ok) {
        const data = await response.json();
        setReminders(data);
      }
    } catch (error) {
      console.error("‚ùå Error fetching reminders:", error);
    }
  };
*/
const fetchReminders = async () => {
  const userId = localStorage.getItem("userId");

  try {
    const response = await fetch(`http://localhost:3000/api/reminders/${userId}`);
    if (response.ok) {
      const data = await response.json();
      setReminders(data);
    }
  } catch (error) {
    console.error("‚ùå Error fetching reminders:", error);
  }
};

  useEffect(() => {
    fetchReminders(); // ‚úÖ Fetch reminders on load
  }, []);

  const handleInputChange = (e) => {
    setNewReminder({ ...newReminder, [e.target.name]: e.target.value });
  };
/*
  const addReminder = async () => {
    if (newReminder.date && newReminder.time && newReminder.note) {
      const dateTime = `${newReminder.date}T${newReminder.time}:00`;

      const response = await fetch("http://localhost:3000/api/reminders/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ dateTime, note: newReminder.note }),
      });

      if (response.ok) {
        alert("‚úÖ Reminder added successfully!");
        setNewReminder({ date: "", time: "", note: "" });
        fetchReminders(); // ‚úÖ Update reminders list
      }
    }
  };
*/
