  console.log("üõ†Ô∏è Attempting to delete reminder with ID:", id);

  if (!window.confirm("Are you sure you want to delete this reminder?")) return;

  try {
    const response = await fetch(`http://localhost:3000/api/reminders/delete/${id}`, {
      method: "DELETE",
    });

    const data = await response.json();
    console.log("üöÄ Server Response:", data);
    alert(data.message); // ‚úÖ Show success message

    
    //if (response.ok) {
      //setReminders(reminders.filter((reminder) => reminder._id !== id)); // ‚úÖ Update UI
    //}
  //} catch (error) {
    //console.error("‚ùå Error deleting reminder:", error);
    //alert("‚ùå Failed to delete reminder!");
