  if (response.ok) {
    alert("‚úÖ Reminder deleted successfully!");
    fetchReminders(); // ‚úÖ Refresh list after deletion
  } else {
    alert(`‚ùå Failed to delete reminder! Server says: ${data.message}`);
  }
} catch (error) {
  console.error("‚ùå Error deleting reminder:", error);
  alert("‚ùå Could not connect to the server.");
}

