      return res.status(400).json({
        message: "Please provide all details"
      });
    }
    const newCycle = new Cycle({
      userId: req.user._id,
      startDate,
      cycleLength
    });
    await newCycle.save();
    res.status(201).json({
      message: "Cycle data saved successfully!"
    });
  } catch (error) {
    console.error("Error in /log API:", error);
    res.status(500).json({
