// src/pages/SplashScreen.js
import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/SplashScreen.css";
import logo from "./assets/Logo.jpg"; // Update with your logo path

const SplashScreen = () => {
  const navigate = useNavigate();

  return (
    <div className="splash-container">
      <div className="splash-left">
        <img src={logo} alt="CycleCare Logo" className="splash-logo" />
      </div>
      <div className="splash-right">
        <h1 className="splash-title">CycleCare</h1>
        <p className="splash-intro">Your personal menstrual cycle and health tracker.</p>
        <button className="splash-btn register" onClick={() => navigate("/register")}>Register</button>
        <button className="splash-btn login" onClick={() => navigate("/login")}>Login</button>
      </div>
    </div>
  );
};

export default SplashScreen;
