import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate hook
import "./SplashScreen.css";

const SplashScreen = () => {
  const navigate = useNavigate(); // Initialize navigation
  const [fade, setFade] = useState(false);

  useEffect(() => {
    setTimeout(() => setFade(true), 1000); // Add fade effect after 1 sec
  }, []);

  return (
    <div className={`splash-container ${fade ? "fade-out" : ""}`}>
      <div className="splash-content">
        <img src="/logo.png" alt="CycleCare Logo" className="logo" />
        <h1>Welcome to CycleCare</h1>
        <p>Your personalized cycle tracking and health assistant.</p>
        <div className="buttons">
          <button className="register" onClick={() => navigate("/register")}>
            Register
          </button>
          <button className="login" onClick={() => navigate("/login")}>
            Log In
          </button>
        </div>
      </div>
    </div>
  );
};

export default SplashScreen;
